import React from 'react'
import {Link} from 'react-router-dom'

const TagList = ({tags}) => {
    return(
        <ul className='tag-list pull-xs-right'  >
                        {tags.map((tag) => {
                            return (
                                
                                         
                                        <Link to={`/tags/${tag}`} key={tag}  className='tag-default tag-pill tag-outline ' >
                                            {tag}
                                        </Link>
                            
                                  
                            )
                        })}
        </ul>
    )
}

export default TagList