import React, { useEffect } from 'react'
import {Link} from 'react-router-dom'

import useFetch from 'hooks/useFetch'
import TagList from 'components/tagList'


const Article = props => {
    const slug = props.match.params.slug
    const apiURL = `/articles/${slug}`
    const [{response, error, isLoading}, doFetch] = useFetch(apiURL)

    useEffect(() => {
        doFetch()
    },[doFetch])

    return (
        <div className='article-page'>
            <div className='banner'>
                {!isLoading && response &&  (
                    <div className='container'>
                        <h1>{response.article.title}</h1>
                        <div className='article-meta'>
                            <Link to={`/propfiles/${response.article.author.username}`}>
                                <img src={response.article.author.image} alt=''/>
                            </Link>
                            <div className='info'>
                            <Link to={`/propfiles/${response.article.author.username}`}>
                            {response.article.author.username}
                            </Link>
                            <span className='date'>{response.article.createdAt}</span>
                            </div>
                        </div>
                    </div>
                )}
            </div>
            <div className='container page'>
                {isLoading && <div>Loading...</div>}
                {error && <div>Sometimes errors happend</div>}
                {!isLoading && response && (
                    <div className='row article-content'>
                        <div className='col-xs-12'>
                            <div>
                                <p>{response.article.body}</p>
                            </div>
                            <TagList tags={response.article.tagList}/>
                        </div>
                    </div>
                )}
            </div>
        </div>
    )
}
export default Article