import React, {useEffect,useContext, useState, Fragment} from 'react'
import {Link, NavLink} from 'react-router-dom'
import Feed from "components/feed";
import useFetch from 'hooks/useFetch'

import {CurrentUserContext} from 'contexts/currentUser'
import Pagination from 'components/pagination'
import {limit, getPaginator} from 'utility/utuils'
import {stringify} from 'query-string'
import PopularTags from 'components/popularTags'

import FeedToggler from 'components/feedToggler'

const FollowFeed = (props)=> {



   
const {offset, currentPage} = getPaginator(props.location.search)
 const url = props.match.url
 console.log('offset', offset)
//  используя  'query-string'
 const stringifyParams = stringify({     
     limit,
     offset
 })
const [currentUserState] = useContext(CurrentUserContext)
const apiURL = `/articles/feed?${stringifyParams}`

const [{response,isLoading,error}, doFetch] = useFetch(apiURL)
const {isLogged} = currentUserState 

useEffect(() => {
    doFetch()
},[doFetch,currentPage, offset])


   return (
    <React.Fragment>       
            {isLogged &&
             <div className='home-page'>
                 <div className='banner'>
                     <div className='container'>
                         <h1>Medium clone</h1>
                         <p>A place to share knowladge</p>
                     </div>
                 </div>
                 <div className='container page'>
                     <div className='row'>
                         <div className='col-md-9'>
                             <FeedToggler  />  
                            
                            {isLoading && <div>Loading...</div>}
                            {error && <div>Sometimes errors happend</div>}
                            {!isLoading && response && 
                            <Fragment>
                                 {response.articles.length === 0 && <div className='article-preview'>No articles are here... yet.</div>}               
                                <Feed articles={response.articles} />
                                <Pagination total={response.articlesCount}limit={limit} url={url} currentPage={currentPage} />
                                                
                            </Fragment>}
                                
                         </div>
                         <div className='col-md-3'>
                             <PopularTags/>
                         </div>
                     </div>
                 </div>
             </div>
                }

            
        
         
 
         
    </React.Fragment>
       
)}
export default FollowFeed