import {Switch,Route} from 'react-router-dom'
import React from 'react'

import GlobalFeed from 'pages/globalFeed'
import CreateArticle from 'pages/createArticle'
import EditArticle from 'pages/editArticle'
import Article from 'pages/article'
import Authentication from 'pages/authenticaion'
import TagFeed from 'pages/tagFeed'
import YourFeed from 'pages/yourFeed'
import FollowFeed from 'pages/followFeed'
import Settings from 'pages/settings'
import UserProfile from 'pages/userProfile'
import Authorized from 'modal/authorized'

const Routes = () => {
    return(
        <Switch>
            <Route path='/' component={GlobalFeed} exact/>
            <Route path='/authorized' component={Authorized} />
            <Route path='/profiles/:slug' component={UserProfile} />
            <Route path='/profiles/:slug/favorites' component={UserProfile} />  
            <Route path='/settings' component={Settings} />
            <Route path='/articles/new' component={CreateArticle} />
            <Route path='/articles/:slug/edit' component={EditArticle} />
            <Route path='/feed' component={YourFeed} />
            <Route path='/feeds' component={FollowFeed} />
            <Route path='/tags/:slug' component={TagFeed} />
            <Route path='/login' component={Authentication} />
            <Route path='/register' component={Authentication} />
            <Route path='/articles/:slug' component={Article}/>
        </Switch>
    )
}

export default Routes