import React, {useEffect, Fragment} from 'react'
import { stringify } from 'query-string'

// import {}
import {getPaginator, limit} from 'utility/utuils'
import useFetch from 'hooks/useFetch'
import Feed from 'components/feed'
import Pagination from 'components/pagination'

const getApiUrl = ({username , offset, isFavorites}) => {
    const params = isFavorites
    ? {limit, offset, favorited: username}
    : {limit, offset, author: username}

    return `/articles?${stringify(params)}`
}
 const UserArticles = ({username, location, isFavorites, url, slug}) => {
     const {offset, currentPage} = getPaginator(location.search)
     const apiURL = getApiUrl({username, offset, isFavorites})
     const [{response, isLoading, error}, doFetch] = useFetch(apiURL)
    
    const favoriteUrl = !isFavorites
    ? url
    : `${url}/favorites`
     console.log('url', favoriteUrl)

     useEffect(() => {
        doFetch()
    },[doFetch, isFavorites,currentPage,username])

     return (
         <div>
             {isLoading && <div>Loading...</div>}
                            {error && <div>Sometimes errors happend</div>}
                            {!isLoading && response && 
                            <Fragment>
                                                
                                <Feed articles={response.articles} />
                                <Pagination total={response.articlesCount}limit={limit} url={favoriteUrl} currentPage={currentPage} />
                                                
                            </Fragment>}
         </div>
     )
 }

 export default UserArticles